import streamlit as st
import tensorflow as tf
import numpy as np
import pandas as pd
from tensorflow.keras.models import Sequential
from tensorflow.keras.layers import Dense
from tensorflow.keras.optimizers import Adam, SGD


ml = tf.keras.models.load_model("grading_model")

st.image('data:image/png;base64,')
st.title("AI Grading Application")

with st.form(key='my_form'):
    s1 = st.number_input('urdu')
    s2 = st.number_input('eng')
    s3 = st.number_input('maths')
    s4 = st.number_input('isl')
    s5 = st.number_input('chem')
    sbt = st.form_submit_button('Login')

if sbt:
    st.write("Subjets",s1,s2,s3,s4,s5)
    labels = ['A', 'A+', 'B', 'C', 'D', 'E', 'Fail']
    result = labels[np.argmax(ml.predict([[s1,s2,s3,s4,s5]]))]
    st.write(result)
    st.header("Result", result)